sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    // rightnow stores the current time
    var rightnow = new GlideDateTime();
    // Create a GlideDateTime object for the When needed date
    var whenNeeded = new GlideDateTime(current.u_when_needed);

	gs.addInfoMessage(rightnow.getDate() + " = " + whenNeeded.getDate() + " ? " + rightnow.getDate() === whenNeeded.getDate());

    // If the When needed date is before rightnow, do not write the record to the database
    // Output an error message to the screen
    if (rightnow.getDate() === whenNeeded.getDate()) {
        gs.addErrorMessage("When needed date cannot be today's date. Please allow for more time. Your request has not been saved to the database.");
        current.setAbortAction(true);

    } else if (whenNeeded.before(rightnow)) {
        gs.addErrorMessage("When needed date cannot be in the past.  Your request has not been saved to the database.");
        current.setAbortAction(true);
    }
})(current, previous);
